import {
    AppstoreOutlined,
    ContainerOutlined,
    DesktopOutlined,
    MailOutlined,
    MenuFoldOutlined,
    MenuUnfoldOutlined,
    PieChartOutlined,
} from '@ant-design/icons';
import { Button, Menu } from 'antd';
import React, { useState,useEffect } from 'react';
import './Menu.scss'
import { Route, Routes, useNavigate } from "react-router-dom";
import Home from "../Routes/Home"
import About from '../Routes/About';

function getItem(label, key, icon, children, type) {
    return {
        key,
        icon,
        children,
        label,
        type,
    };
}

const items = [
    getItem('Option 1', '1', <PieChartOutlined />),
    getItem('Option 2', '2', <DesktopOutlined />),
    getItem('Option 3', '3', <ContainerOutlined />),
    getItem('Navigation One', 'sub1', <MailOutlined />, [
        getItem('Option 5', '5'),
        getItem('Option 6', '6'),
        getItem('Option 7', '7'),
        getItem('Option 8', '8'),
    ]),
    getItem('Navigation Two', 'sub2', <AppstoreOutlined />, [
        getItem('Option 9', '9'),
        getItem('Option 10', '10'),
        getItem('Submenu', 'sub3', null, [getItem('Option 11', '11'), getItem('Option 12', '12')]),
    ]),
];

const MENU = () => {
    const [collapsed, setCollapsed] = useState(false);

    const toggleCollapsed = () => {
        setCollapsed(!collapsed);
    };
    const navigate = useNavigate()
    // const location = useLocation()
    const todoMenu = ({ key, keyPath }) => {
        console.log(key, keyPath)
        navigate(`/${key}`)
    }
    return (
        <div className='main'>
            <div className='header_ma'>
                <Button
                    className='but_m'
                    type="primary"
                    onClick={toggleCollapsed}
                    style={{
                        marginBottom: 16,
                    }}
                >
                    {collapsed ? <MenuUnfoldOutlined /> : <MenuFoldOutlined />}
                </Button>
            </div>
            <div className='esebody'>
                <div className='esere'>
                    <Menu
                        className='meau_ul'
                        defaultSelectedKeys={['1']}//初始选中的菜单项 key 数组
                        defaultOpenKeys={['1']}//初始展开的 SubMenu 菜单项 key 数组
                        mode="inline"
                        theme="dark"
                        inlineCollapsed={collapsed}
                        items={items}
                        onClick={todoMenu}
                    />
                </div>
                <div className='esele'>
                    <Routes>
                        <Route exact path="1" element={<Home />} />
                        <Route path="2" element={<About />} />
                    </Routes>
                </div>
            </div>
        </div>
    );
};

export default MENU;
